import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger
} from '@/components/ui/tooltip'
import { SVGProps, useCallback } from 'react'
import { Link, useLocation, useNavigate } from 'react-router-dom'
import { twMerge } from 'tailwind-merge'

const sidebarItemClass =
  'flex items-center gap-3 rounded-lg px-3 py-2 transition-all'
const sidebarItemDefaultClass =
  'text-gray-500 hover:text-gray-900 dark:text-gray-400 dark:hover:text-gray-50'
const sidebarItemActiveClass =
  'bg-gray-100 text-gray-900 hover:text-gray-900 dark:bg-gray-800 dark:text-gray-50 dark:hover:text-gray-50'

const SidebarNav = () => {
  const location = useLocation()
  const navigate = useNavigate()
  const getCn = useCallback(
    (path: string) => {
      return twMerge(
        sidebarItemClass,
        sidebarItemDefaultClass,
        location.pathname == path && sidebarItemActiveClass
      )
    },
    [location.pathname]
  )
  return (
    <nav className="grid items-start px-4 text-sm font-medium">
      <Link to={`/`} className={getCn('/')}>
        <HomeIcon className="size-4" />
        <span className="flex items-center flex-1 justify-between">
          Docs
          <TooltipProvider delayDuration={250}>
            <Tooltip>
              <TooltipTrigger>
                <span
                  onClick={(e) => {
                    e.preventDefault()
                    e.stopPropagation()
                    navigate('/new')
                  }}
                >
                  <svg
                    width="15"
                    height="15"
                    viewBox="0 0 15 15"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <path
                      d="M7.49991 0.876892C3.84222 0.876892 0.877075 3.84204 0.877075 7.49972C0.877075 11.1574 3.84222 14.1226 7.49991 14.1226C11.1576 14.1226 14.1227 11.1574 14.1227 7.49972C14.1227 3.84204 11.1576 0.876892 7.49991 0.876892ZM1.82707 7.49972C1.82707 4.36671 4.36689 1.82689 7.49991 1.82689C10.6329 1.82689 13.1727 4.36671 13.1727 7.49972C13.1727 10.6327 10.6329 13.1726 7.49991 13.1726C4.36689 13.1726 1.82707 10.6327 1.82707 7.49972ZM7.50003 4C7.77617 4 8.00003 4.22386 8.00003 4.5V7H10.5C10.7762 7 11 7.22386 11 7.5C11 7.77614 10.7762 8 10.5 8H8.00003V10.5C8.00003 10.7761 7.77617 11 7.50003 11C7.22389 11 7.00003 10.7761 7.00003 10.5V8H4.50003C4.22389 8 4.00003 7.77614 4.00003 7.5C4.00003 7.22386 4.22389 7 4.50003 7H7.00003V4.5C7.00003 4.22386 7.22389 4 7.50003 4Z"
                      fill="currentColor"
                      fillRule="evenodd"
                      clipRule="evenodd"
                    ></path>
                  </svg>
                </span>
              </TooltipTrigger>
              <TooltipContent>Write new doc</TooltipContent>
            </Tooltip>
          </TooltipProvider>
        </span>
      </Link>
      <Link to={`/query`} className={getCn('/query')}>
        <QueryIcon className="size-4" />
        Query
      </Link>
      <Link to="/integrations" className={getCn('/integrations')}>
        <IntegrationsIcon className="size-4" />
        Integrations
      </Link>
      <Link to="/widgets" className={getCn('/widgets')}>
        <WidgetsIcon className="size-4" />
        Widgets
      </Link>
      <Link to="/users" className={getCn('/users')}>
        <UsersIcon className="size-4" />
        Users
      </Link>
      <Link to="/settings" className={getCn('/settings')}>
        <SettingsIcon className="size-4" />
        Settings
      </Link>
      {/* <Link
        to="/"
        className={twMerge(sidebarItemClass, sidebarItemDefaultClass)}
      >
        <UsersIcon className="size-4" />
        Analytics
      </Link> */}
    </nav>
  )
}

